function getJsonFromString(json)
{
	var str = JSON.stringify(json); //json.toString();
	var start = str.indexOf('{');
	var end = str.indexOf('}');
	str = str.substring(start, end);
	str.replace(/&quot;/ig,'"');
	//str = JSON.parse('['+str+']');
	return (str); 
}

function objToString (obj) {
    var str = '';
    for (var p in obj) {
        if (obj.hasOwnProperty(p)) {
            str += p + '::' + obj[p] + '\n';
        }
    }
    return str;
}

