  function RandomColor() {
    var letters = '0123456789ABCDEF';
    var color = '#';
    for (var i = 0; i < 6; i++ ) {
        color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }
 
 
 function getRandomColors(length) {
	var colors = [];
	for (var i = 0; i < length; i++) 
		colors[i]=RandomColor();
	return colors;
}


